<template>
  <div>
    <button
      v-if="this.btns.info"
      class="info"
      :class="this.btnsClass"
      @click="ThreeDModal($event)"
    >3D</button>
    <button
      v-if="this.btns.close"
      class="close"
      :class="this.btnsClass"
    >&#10006;</button>
    <button
      v-if="this.btns.cart"
      class="basket icon-cart"
      :class="this.btnsClass"
    />
    <button
      v-if="this.btns.delete"
      class="delete icon-bascet"
      :class="this.btnsClass"
    />
    <button
      v-if="this.btns.like"
      class="product-btn liked"
      :class="this.btnsClass"
    >‚ù§</button>
  </div>
</template>

<script>
export default {
  props: ['item', 'btns', 'volume'],
  data () {
    return {
      card: this.item,
      btn: this.btns
    }
  },
  computed: {
    btnsClass () {
      if (this.volume === '2d') {
        return 'product-btn'
      } else {
        return 'header-icon'
      }
    }
  },
  methods: {
    closeModal () {
      this.$store.commit('EXISTENCE_ITEM', false)
      this.$store.dispatch('enablesScroll')
    },
    ThreeDModal (e) {
      this.$store.dispatch('CardInfoModal', e)
    }
  }
}
</script>

